mapper.insert:
 INSERT INTO EMP_MEMBER(   MEMBER_IDX 
					       , USER_ID 
					       , USER_PWD
					       , USER_NAME
					       , USER_BNAME
					       , USER_CRCODE
					       , HP_NUM
					       , USER_EMAIL
					       , USER_AUTH
					       , REG_DATE
					       , MOD_DATE 	)

               VALUES(    
               			SEQ_MEMBER_IDX.NEXTVAL
               		   , #{user_id}
              		   , #{user_pwd}
              		   , #{user_name}
              		   , #{user_bname}
              		   , #{user_crcode}
              		   , #{hp_num}
              		   , #{user_email}
              		   , #{user_auth}
              		   , SYSDATE
              		   , SYSDATE

              		   )

mapper.update:
UPDATE EMP_MEMBER SET 

						USER_ID = #{user_id},
						USER_PWD = #{userPwd},
						USERNAME = #{userName}
						USER_EMAIL = #{user_email}
						HP_NUM = #{hp_num}
						USER_CRCODE = #{user_crcode}

WHERE USER_ID = #{user_id}

mapper.delete:
DELETE FROM EMP_MEMBER
WHERE USER_ID = #{user_id}
AND USER_PWD = #{user_pwd}

mapper.select:
SELECT COUNT(*) FROM EMP_MEMBER
WHERE USER_ID = #{userId}
  AND USER_PWD = #{user_pwd}
