mapper.select:
SELECT BPM_BPLAN_IDX , BOARD_IDX , TITLE_REMARK1 , TITLE_REMARK2 , TITLE_REMARK3
, WRITER , REG_DATE , MOD_DATE , STATUS FROM ( SELECT BPM_BPLAN_IDX , BOARD_IDX , TITLE_REMARK1 , TITLE_REMARK2 , TITLE_REMARK3 , WRITER , REG_DATE , MOD_DATE , STATUS ,ROW_NUMBER() OVER(ORDER BY BPM_BPLAN_IDX DESC) AS RNUM FROM EMP_BPM_BPLAN WHERE 1=1 AND BAM_ANC_IDX = #{bam_anc_idx} AND DEL_YN = 'N'
) MP WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd} ORDER BY BPM_BPLAN_IDX DESC

SELECT BPM_BPLAN_IDX , BOARD_IDX , TITLE_REMARK1 , TITLE_REMARK2 , TITLE_REMARK3 , WRITER , REG_DATE , MOD_DATE , STATUS , WRITER_ID FROM ( SELECT BPM_BPLAN_IDX , BOARD_IDX , TITLE_REMARK1 , TITLE_REMARK2 , TITLE_REMARK3 , WRITER , REG_DATE , MOD_DATE , STATUS , WRITER_ID
,ROW_NUMBER() OVER(ORDER BY BPM_BPLAN_IDX DESC) AS RNUM FROM EMP_BPM_BPLAN WHERE 1=1 AND WRITER_id = #{user_id} AND DEL_YN = 'N'
) MP WHERE RNUM BETWEEN #{rowStart} AND #{rowEnd} ORDER BY BPM_BPLAN_IDX DESC

