mapper.insert:
 INSERT INTO EMP_MEMBER(   MEMBER_IDX 
					       , USER_ID 
					       , USER_PWD
					       , USER_NAME
					       , USER_BIRTHDAY
					       , HP_NUM
					       , USER_EMAIL
					       , USER_AUTH
					       , REG_DATE
					       , MOD_DATE 	)

               VALUES(    
               			SEQ_MEMBER_IDX.NEXTVAL
               		   , #{user_id}
              		   , #{user_pwd}
              		   , #{user_name}
              		   , #{user_birthday}
              		   , #{hp_num}
              		   , #{user_email}
              		   , '0'
              		   , SYSDATE
              		   , SYSDATE

              		   )

mapper.update:
UPDATE EMP_MEMBER SET 

USERNAME = #{userName}
WHERE USER_ID = #{user_id}

