mapper.insert:
 INSERT INTO EMP_MEMBER(   MEMBER_IDX 
					       , USER_ID 
					       , USER_PWD
					       , USER_NAME
					       , USER_BIRTHDAY
					       , HP_NUM
					       , USER_EMAIL
					       , USER_AUTH
					       , REG_DATE
					       , MOD_DATE 	)

               VALUES(    
               			SEQ_MEMBER_IDX.NEXTVAL
               		   , #{userId}
              		   , #{userPwd}
              		   , #{userName}
              		   , #{userBirthday}
              		   , #{hpNum}
              		   , #{userEmail}
              		   , #{userAuth}
              		   , SYSDATE
              		   , SYSDATE

              		   )

mapper.update:
UPDATE EMP_MEMBER SET 

USERNAME = #{userName}
WHERE USER_ID = #{userId}

mapper.select:
SELECT COUNT(*) FROM EMP_MEMBER
WHERE USER_ID = #{userId}
  AND USER_PWD = #{userPwd}
